                        | {{ $t(`menu.${item.path}`) }}                               

</template>

<script>
export default {
  data() {
    return {
      isActive: false
    };
  },
  computed: {
    menu() {
      return this.$store.state.menu;
    },
    visitedData() {
      return this.$store.state.visitedData;
    },
    scrolledData() {
      if (this.$route.path !== "/") {
        return true;
      } else {
        return this.$store.state.scrolledData;
      }
    }
  },
  methods: {
    visited(path) {
      this.$store.commit("changeVisitedData", path);
    },
    toggleNavMobile() {
      this.isActive = !this.isActive;
    }
  }
};
</script>
